Scripts.OnHide:
for entry, name in next, UISpecialFrames do if (name == self:GetName()) then tremove(UISpecialFrames, entry) end end
self.button:SetButtonState("NORMAL",0);

PlaySound("igCharacterInfoClose");
FeedbackUI_Hide();
FeedbackUI_SetupWelcome();

Scripts.OnTextChanged:
if ( ( self:GetText() ~= self.default ) and ( self:GetText() ~= self.blankString ) ) then
self.parentform.submit:Enable();
else
self.parentform.submit:Disable();
end
ScrollingEdit_OnTextChanged(self);

Scripts.OnLoad:
self.scroll = self:GetParent():GetParent();
if ( not self.scroll.buttons ) then
self.scroll.buttons = {}
end
table.insert(self.scroll.buttons, self);
self.index = table.maxn(self.scroll.buttons);
self.name = self:GetName();
self.text = getglobal(self.name.."Text");
self.rightText = getglobal(self.name.."RightText");
self.func = nil;
self.text:SetText(self.name);
self.highlightColor = { ["r"] = 1, ["g"] = 1, ["b"] = 1, ["a"] = 1 };
self.normalColor = { ["r"] = 1, ["g"] = .82, ["b"] = 0, ["a"] = 1 };

self:Raise();
self:Disable();
self.scroll = self:GetParent():GetParent();
self.panel = self.scroll:GetParent();
self.scroll.upbtn = self;

self:Raise();
self:Disable();
self.scroll = self:GetParent():GetParent();
self.panel = self.scroll:GetParent();
self.scroll.downbtn = self;

self:Raise();
self:Disable();
self.scroll=self:GetParent():GetParent():GetParent();
self.scroll.thumb = self;
self.panel = self.scroll:GetParent();

self.scroll = self:GetParent();
self.name = self:GetName();
self.text = getglobal(self.name.."Text");
self.rightText = getglobal(self.name.."RightText");
self.func = nil;
self.text:SetText(self.name);
self.highlightColor = { ["r"] = 1, ["g"] = 1, ["b"] = 1, ["a"] = 1 };
self.normalColor = { ["r"] = 1, ["g"] = .82, ["b"] = 0, ["a"] = 1 };

Scripts.OnEnter:
self.btn:LockHighlight();
self.text:SetTextColor(self.highlightColor.r, self.highlightColor.g, self.highlightColor.b, self.highlightColor.a);
self.rightText:SetTextColor(self.highlightColor.r, self.highlightColor.g, self.highlightColor.b, self.highlightColor.a);
if ( self.tooltip ) then
self.scroll:GetParent().ShowTooltip(self.scroll:GetParent(), self)
end

Scripts.OnLeave:
self.btn:UnlockHighlight();
self.text:SetTextColor(self.normalColor.r, self.normalColor.g, self.normalColor.b, self.normalColor.a);
self.rightText:SetTextColor(self.normalColor.r, self.normalColor.g, self.normalColor.b, self.normalColor.a);
if ( self.tooltip ) then
self.scroll:GetParent().HideTooltip(self.scroll:GetParent())
end

Scripts.OnEnter:
local parent = self.parent
parent.text:SetTextColor(parent.highlightColor.r, parent.highlightColor.g, parent.highlightColor.b, parent.highlightColor.a);
parent.rightText:SetTextColor(parent.highlightColor.r, parent.highlightColor.g, parent.highlightColor.b, parent.highlightColor.a);

Scripts.OnLeave:
local parent = self.parent
parent.text:SetTextColor(parent.normalColor.r, parent.normalColor.g, parent.normalColor.b, parent.normalColor.a);
parent.rightText:SetTextColor(parent.normalColor.r, parent.normalColor.g, parent.normalColor.b, parent.normalColor.a);

Scripts.OnClick:
PanelTemplates_Tab_OnClick(self, FeedbackUI);
PlaySound("igCharacterInfoTab");
for _, frame in next, feedbackFrames do
frame:Hide();
end
self.frame:Show();
if ( self.frame.panels ) then
for _, panel in pairs(self.frame.panels) do
pcall(panel.Show, panel);
end
end

Scripts.OnLoad:
self:RegisterForClicks("LeftButtonUp");
self:SetHeight("42");
self:SetWidth("34");
self.alert = self:GetParent();
if ( not self.alert.buttons ) then
self.alert.buttons = {}
end
table.insert(self.alert.buttons, self);
self.index = table.maxn(self.alert.buttons);
self:ClearAllPoints()
self:SetPoint("BOTTOM", "UIParent", "BOTTOM", 0, 130)
self.tooltip = "";
self.lines = {}

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_LEFT");
for _, line in ipairs(self.lines) do
GameTooltip:AddDoubleLine(line.left, line.right);
end

GameTooltipTextLeft2:SetText(FEEDBACKUI_BLUE .. ( GameTooltipTextLeft2:GetText() or "" ) )

if ( SHOW_NEWBIE_TIPS == "1" ) then
GameTooltip:AddLine(FEEDBACKUI_SURVEYNEWBIETEXT, NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b, 1);
end
GameTooltip:Show();

Scripts.OnClick:
FeedbackUISurveyFrameSurveysPanelDdlCategory:SetSelectedValue("All");
FeedbackUISurveyFrameSurveysPanelDdlStatus:SetSelectedValue("Available");
self.alert.panel.Expand(self.alert.panel, g_FeedbackUI_surveysTable["Alerts"][self.index].type)
self.alert.panel.table = {};
self.alert.panel.PopulateTable(self.alert.panel)
self.alert.panel.ScrollToSurvey(self.alert.panel, g_FeedbackUI_surveysTable["Alerts"][self.index])
if ( self:IsVisible() ) then
GameTooltip:SetOwner(self, "ANCHOR_LEFT");
for _, line in ipairs(self.lines) do
GameTooltip:AddDoubleLine(line.left, line.right);
end

GameTooltipTextLeft2:SetText(FEEDBACKUI_BLUE .. GameTooltipTextLeft2:GetText())

if ( SHOW_NEWBIE_TIPS == "1" ) then
GameTooltip:AddLine(FEEDBACKUI_SURVEYNEWBIETEXT, NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b, 1);
end
GameTooltip:Show();
end

if ( self.sortType ) then
self.panel.SortResults(self.panel, self.sortType);
end
PlaySound("igMainMenuOptionCheckBoxOn");
